import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-category-form',
  templateUrl: './category-form.component.html',
  styleUrls: ['./category-form.component.css']
})
export class CategoryFormComponent {

  public mForm: FormGroup;
  public isUpdateMode:boolean = false; 
  public header: string = "CategoryFormComponent";

  constructor(private http : HttpClient, @Inject('BASE_URL') private baseUrl: string, private dialg: MatDialogRef<any>) {
    this.mForm = new FormGroup({
      name: new FormControl('', [Validators.required, Validators.minLength(2)]),
      categoryId: new FormControl('', [Validators.minLength(10)])
    });
  }

  public onSubmit(){
    if(this.mForm.valid){
      if(this.isUpdateMode){
        //this.update();
      }else{
        this.insert();
      }
    }else{
      console.log("TODO: processing Validators");
    }
  }

  public onCancel(){

  }

  private insert(){
    this.http.put<any>(this.baseUrl + "i/Category", this.mForm.value,
    {
      headers: new HttpHeaders().set("Authorization", "Bearer " + localStorage.getItem(TOKEN))
    })
    .subscribe(result => {
      console.log(result);

      var newCategory = new Category();
      newCategory.id = result['id'];
      newCategory.name = this.mForm.controls['name'].getRawValue();
      newCategory.categoryId = this.mForm.controls['categoryId'].getRawValue();
      this.list.push(newCategory);
      this.mForm.reset();
    }, error => {
      console.log(error);
    });

  }

}
<p *ngIf="header != ''" class="h5 text-center my-1">{{header}}</p>
<form mat-dialog-content [formGroup]="mForm" (ngSubmit)="onSubmit()">
    <div class="form-group my-2">
        <input class="form-control" formControlName="name" placeholder="name" type="text">
    </div>
    <div class="form-group my-2">
      <input class="form-control" formControlName="categoryId" placeholder="categoryId" type="text">
    </div>

    <div mat-dialog-actions align="end">
        <button *ngIf="!isUpdateMode && mForm.valid" class="btn btn-primary w-25" type="submit">add</button>
        <button *ngIf="!isUpdateMode && !mForm.valid" class="btn btn-primary w-25" type="submit" disabled>add</button>
    </div>

    <div *ngIf="isUpdateMode" mat-dialog-actions align="end">
      <button mat-button cdkFocusInitial *ngIf="mForm.valid" class="btn btn-primary mx-1" type="submit">update</button>
      <button mat-button cdkFocusInitial *ngIf="!mForm.valid" class="btn btn-primary mx-1" type="submit" disabled>update</button>
      <button class="btn btn-primary mx-1" (click)="onCancel()">cancel</button>
    </div>
</form>
